// POINT Reactでの差分検知
import { useState } from "react";
import "./Example.css";

const Example = () => {
  console.log("render");
  
  const [countA, setCountA] = useState({
    val: 0
  });

  const obj1 = {val:0};
  const obj2 = obj1;
  
  const isSame1 = Object.is(obj1,obj2);
  console.log("a: " + isSame1);

  return (
    <div className="parent">
      <div>
        <h3>再レンダリング？</h3>
        <button
          onClick={() => {
            setCountA(1);
          }}
        >
          ボタンA
        </button>
      </div>
      <div>
        <p>ボタンAクリック回数：{countA.val}</p>
      </div>
    </div>
  );
};

export default Example;
